import { useRef, useState } from 'react';
const SimpleInput = (props) => {
    const nameInputRef = useRef();
    const [enteredName, setEnteredName] = useState('');
    const [enteredNameIsValid, setEnteredNameIsValid] = useState(false); //later on, we might be able to add useEffect with the dependency of this state
    const [enteredNameTouched, setEnteredNameTouched] = useState(false);


    const nameInputChangedHandler = event => {
        setEnteredName(event.target.value);

        if(event.target.value.trim() !== ''){
            setEnteredNameIsValid(true);
        }
    }

    const nameInputBlurHandler = event => {
        setEnteredNameTouched(true);

        if(enteredName.trim() === '') {
            setEnteredNameIsValid(false);
        }
    }

    const formSubmissionHandler = event => {
        event.preventDefault();

        setEnteredNameTouched(true);

        if(enteredName.trim() === '') {
            setEnteredNameIsValid(false);
            return;
        }
        setEnteredNameIsValid(true);

        setEnteredName('');
        // nameInputRef.current.value = '';
        // -> NOT IDEAL, DON'T MANIPULATE THE DOM DIRECTLY.
    }

    const nameInputIsInvalid = !enteredNameIsValid && enteredNameTouched;

    const nameInputClasses = nameInputIsInvalid ? 'form-control invalid' : 'form-control'
  return (
    <form onSubmit={formSubmissionHandler}>
      <div className={nameInputClasses}>
        <label htmlFor='name'>Your Name</label>
        <input ref={nameInputRef} type='text' id='name' onChange={nameInputChangedHandler} onBlur={nameInputBlurHandler} value={enteredName}/>
          {nameInputIsInvalid && <p className="error-text">
              Name must not be empty.
          </p>}
      </div>
      <div className="form-actions">
        <button>Submit</button>
      </div>
    </form>
  );
};

export default SimpleInput;
