import {useState} from 'react';
import useInputs from "../hooks/use-inputs";
const SimpleInput = (props) => {
    const {
        value: enteredName,
        isValid: enteredNameIsValid,
        hasError: nameInputHasError,
        valueChangeHandler: nameChangedHandler,
        inputBlurHandler: nameBlurHandler,
        reset: resetNameInput
    } = useInput(value => value.trim() !== '');

    const [enteredEmail, setEnteredEmail] = useState('');
    const [enteredNameTouched, setEnteredNameTouched] = useState(false);
    const [enteredEmailTouched, setEnteredEmailTouched] = useState(false);
    const [emailErrorMessage, setEmailErrorMessage] = useState("");


    const VALID_REGEXP = /^[a-zA-Z0-9.!#$%&'*+/=?^_`{|}~-]+@[a-zA-Z0-9-]+(?:\.[a-zA-Z0-9-]+)*$/;
    const enteredEmailIsValid = enteredEmail.trim().match(VALID_REGEXP);
    const emailInputIsInvalid = !enteredEmailIsValid && enteredEmailTouched;

    let formIsValid = false;
    formIsValid = enteredNameIsValid && enteredEmailIsValid;

    const emailInputChangedHandler = event => {
        setEnteredEmail(event.target.value);
    }

    const emailInputBlurHandler = event => {
        setEnteredEmailTouched(true);
        if(event.target.value.length === 0){
            setEmailErrorMessage("Email must not be empty.")
        } else {
            setEmailErrorMessage("type your email in a correct form. (including @)")
        }
    }

    const formSubmissionHandler = event => {
        event.preventDefault();

        setEnteredEmailTouched(true);

        if(!enteredNameIsValid || !enteredEmailIsValid) {
            return;
        }

        setEnteredEmail('');
        setEnteredNameTouched(false);
        setEnteredEmailTouched(false);
        // nameInputRef.current.value = '';
        // -> NOT IDEAL, DON'T MANIPULATE THE DOM DIRECTLY.
    }


    const nameInputClasses = nameInputHasError ? 'form-control invalid' : 'form-control'
    const emailInputClasses = emailInputIsInvalid ? 'form-control invalid' : 'form-control'

  return (
    <form onSubmit={formSubmissionHandler}>
      <div className={nameInputClasses}>
        <label htmlFor='name'>Your Name</label>
        <input type='text' id='name' onChange={nameChangedHandler} onBlur={nameBlurHandler} value={enteredName}/>
          {nameInputHasError && <p className="error-text">
              Name must not be empty.
          </p>}
      </div>
        <div className={emailInputClasses}>
            <label htmlFor='email'>Your Email</label>
            <input type='text' id='email' onChange={emailInputChangedHandler} onBlur={emailInputBlurHandler} value={enteredEmail}/>
            {emailInputIsInvalid && <p className="error-text">
                {emailErrorMessage}
            </p>}
        </div>
        <div className="form-actions">
            <button disabled={!formIsValid}>Submit</button>
        </div>
    </form>
  );
};

export default SimpleInput;
